import React, { useState } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import './App.css'; // Import the new CSS file

// Helper Component for Confidence Bar
const ConfidenceBar = ({ confidence }) => {
  const percent = Math.round(confidence * 100);
  let barColor;
  if (percent > 75) barColor = '#2ecc71'; // High confidence - green
  else if (percent > 50) barColor = '#f1c40f'; // Medium confidence - yellow
  else barColor = '#e74c3c'; // Low confidence - red

  return (
    <>
      <div className="confidence-bar-container">
        <div className="confidence-bar-fill" style={{ width: `${percent}%`, background: barColor }} />
      </div>
      <div className="confidence-bar-text">
        {percent}% Confidence
      </div>
    </>
  );
};

// Main App Component
function App() {
  const [category, setCategory] = useState('Home_and_Kitchen_5');
  const [rating, setRating] = useState('5.0');
  const [review, setReview] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [recentReviews, setRecentReviews] = useState([]);

  // Dashboard summary counts
  const totalAnalyzed = recentReviews.length;
  const fakeCount = recentReviews.filter(r => r.result?.is_fake).length;
  const genuineCount = totalAnalyzed - fakeCount;

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setResult(null);
    try {
      const response = await fetch('http://localhost:5000/predict', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ category, rating, review }),
      });
      const data = await response.json();
      setResult(data);
      setRecentReviews(prev => [
        {
          id: new Date().getTime(), // Unique key for animation
          category,
          rating,
          review,
          result: data,
          timestamp: new Date().toLocaleString()
        },
        ...prev
      ].slice(0, 10)); // Keep only the last 10 reviews
    } catch (error) {
      setResult({ error: 'Could not connect to the analysis server.' });
    }
    setLoading(false);
  };

  const statItems = [
    { label: 'Total Analyzed', value: totalAnalyzed, color: '#8A2BE2' },
    { label: 'Fake Reviews', value: fakeCount, color: '#e74c3c' },
    { label: 'Genuine Reviews', value: genuineCount, color: '#2ecc71' }
  ];

  return (
    <div className="app-container">
      <h1 className="app-header">🕵️‍♂️ Review Guard</h1>

      {/* Dashboard Stats */}
      <div className="stats-grid">
        {statItems.map((stat, i) => (
          <div key={stat.label} className="stat-card" style={{ borderBottomColor: stat.color, animationDelay: `${i * 0.15}s` }}>
            <div className="label" style={{ color: stat.color }}>{stat.label}</div>
            <div className="value">{stat.value}</div>
          </div>
        ))}
      </div>

      {/* Review Analysis Form */}
      <form onSubmit={handleSubmit} className="review-form">
        <div className="inputs-row">
          <select value={category} onChange={e => setCategory(e.target.value)}>
            <option value="Home_and_Kitchen_5">Home & Kitchen</option>
            <option value="Sports_5">Sports</option>
            <option value="Office_5">Office</option>
            <option value="Electronics_5">Electronics</option>
            <option value="Other_5">Other</option>
          </select>
          <input type="number" min="1" max="5" step="0.1" value={rating} onChange={e => setRating(e.target.value)} placeholder="Rating" />
        </div>
        <textarea
          value={review}
          onChange={e => setReview(e.target.value)}
          placeholder="Paste or type a product review here..."
        />
        <button type="submit" className="analyze-button" disabled={loading || !review}>
          {loading ? 'Analyzing...' : 'Analyze Review'}
        </button>
      </form>

      {/* Current Analysis Result */}
      <TransitionGroup>
        {result && (
          <CSSTransition timeout={500} classNames="item">
            <div className={`result-container ${result.is_fake ? 'fake' : 'genuine'}`}>
              {result.error ? (
                <span className="verdict fake">{result.error}</span>
              ) : (
                <>
                  <div className={`verdict ${result.is_fake ? 'fake' : 'genuine'}`}>
                    {result.is_fake ? '🚨 Fake Review Detected' : '✅ Review Appears Genuine'}
                  </div>
                  {result.predicted_label && (
                    <div className="predicted-label">
                      Model classified as: <strong>{result.predicted_label}</strong>
                    </div>
                  )}
                  {result.confidence && result.confidence !== 'N/A' && (
                    <ConfidenceBar confidence={result.confidence} />
                  )}
                </>
              )}
            </div>
          </CSSTransition>
        )}
      </TransitionGroup>

      {/* Recent Analyzed Reviews */}
      {recentReviews.length > 0 && (
        <div className="recent-reviews-section">
          <h3>Recent Analysis</h3>
          <div className="recent-reviews-list">
            <TransitionGroup>
              {recentReviews.map((item) => (
                <CSSTransition key={item.id} timeout={500} classNames="item">
                  <div className={`review-item ${item.result.is_fake ? 'fake' : 'genuine'}`}>
                    <div className="timestamp">{item.timestamp}</div>
                    <p className="review-text">"{item.review}"</p>
                    <div className="review-meta">
                      <span>Category: <strong>{item.category.replace('_5', '')}</strong> | Rating: <strong>{item.rating}</strong></span>
                       <span className={`status ${item.result.is_fake ? 'fake' : 'genuine'}`}>
                         {item.result.is_fake ? 'Fake' : 'Genuine'}
                       </span>
                    </div>
                  </div>
                </CSSTransition>
              ))}
            </TransitionGroup>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
